-- ============================================================
-- ICT 운영관리 - 마스터 테이블 (Oracle)
-- Oracle 12c 이상 IDENTITY 컬럼 사용
-- ============================================================

-- 담당자 마스터 (Staff)
CREATE TABLE "STAFF_MASTER" (
    "STAFF_ID"      NUMBER GENERATED BY DEFAULT AS IDENTITY PRIMARY KEY,  -- 담당자 ID
    "NAME"          VARCHAR2(100) NOT NULL,                 -- 담당자명
    "EMPLOYEE_NO"   VARCHAR2(20),                           -- 사번
    "DEPARTMENT"    VARCHAR2(100),                         -- 부서
    "DEPT_CODE"     VARCHAR2(20),                           -- 부서코드
    "RESPONS"       VARCHAR2(200),                          -- 담당업무
    "EMAIL"         VARCHAR2(255),                         -- 이메일
    "PHONE"         VARCHAR2(50),                          -- 연락처
    "MPHONE"        VARCHAR2(50),                           -- 휴대전화번호
    "STATUS"        VARCHAR2(20) DEFAULT 'ACTIVE',           -- 상태 (ACTIVE/INACTIVE 등)
    "CREATED_AT"    TIMESTAMP DEFAULT SYSTIMESTAMP,         -- 등록일시
    "UPDATED_AT"    TIMESTAMP DEFAULT SYSTIMESTAMP          -- 수정일시
);

COMMENT ON TABLE "STAFF_MASTER" IS '담당자 마스터';
COMMENT ON COLUMN "STAFF_MASTER"."STATUS" IS '사용유무 (Y/N)';
COMMENT ON COLUMN "STAFF_MASTER"."STAFF_ID" IS '담당자 ID';
COMMENT ON COLUMN "STAFF_MASTER"."NAME" IS '담당자명';
COMMENT ON COLUMN "STAFF_MASTER"."EMPLOYEE_NO" IS '사번';
COMMENT ON COLUMN "STAFF_MASTER"."DEPARTMENT" IS '부서';
COMMENT ON COLUMN "STAFF_MASTER"."DEPT_CODE" IS '부서코드';
COMMENT ON COLUMN "STAFF_MASTER"."RESPONS" IS '담당업무';
COMMENT ON COLUMN "STAFF_MASTER"."EMAIL" IS '이메일';
COMMENT ON COLUMN "STAFF_MASTER"."PHONE" IS '연락처';
COMMENT ON COLUMN "STAFF_MASTER"."MPHONE" IS '휴대전화번호';


-- 벤더 마스터 (Vendor)
CREATE TABLE "VENDOR_MASTER" (
    "VENDOR_ID"     NUMBER GENERATED BY DEFAULT AS IDENTITY PRIMARY KEY,  -- 벤더 ID
    "VENDOR_NAME"   VARCHAR2(200) NOT NULL,                 -- 벤더명
    "CONTACT_NAME"  VARCHAR2(100),                         -- 담당자명
    "CONTACT_PHONE" VARCHAR2(50),                           -- 연락처
    "CONTACT_EMAIL" VARCHAR2(255),                           -- 이메일
    "ADDRESS"       VARCHAR2(500),                           -- 주소
    "SALES_REP_NAME"  VARCHAR2(100),                        -- 영업대표 (1인)
    "SALES_REP_PHONE" VARCHAR2(50),                         -- 영업대표 전화번호
    "SALES_REP_EMAIL" VARCHAR2(255),                        -- 영업대표 이메일
    "SUPPORT_1_NAME"  VARCHAR2(100),                        -- 담당자 1
    "SUPPORT_1_PHONE" VARCHAR2(50),                         -- 담당자 1 전화번호
    "SUPPORT_1_EMAIL" VARCHAR2(255),                        -- 담당자 1 이메일
    "SUPPORT_2_NAME"  VARCHAR2(100),                        -- 담당자 2
    "SUPPORT_2_PHONE" VARCHAR2(50),                        -- 담당자 2 전화번호
    "SUPPORT_2_EMAIL" VARCHAR2(255),                        -- 담당자 2 이메일
    "STATUS"        VARCHAR2(20) DEFAULT 'ACTIVE',           -- 상태 (ACTIVE/INACTIVE 등)
    "CREATED_AT"    TIMESTAMP DEFAULT SYSTIMESTAMP,         -- 등록일시
    "UPDATED_AT"    TIMESTAMP DEFAULT SYSTIMESTAMP          -- 수정일시
);

COMMENT ON TABLE "VENDOR_MASTER" IS '벤더 마스터';
COMMENT ON COLUMN "VENDOR_MASTER"."STATUS" IS '사용유무 (Y/N)';
COMMENT ON COLUMN "VENDOR_MASTER"."VENDOR_ID" IS '벤더 ID';
COMMENT ON COLUMN "VENDOR_MASTER"."VENDOR_NAME" IS '벤더명';
COMMENT ON COLUMN "VENDOR_MASTER"."CONTACT_NAME" IS '담당자명';
COMMENT ON COLUMN "VENDOR_MASTER"."CONTACT_PHONE" IS '연락처';
COMMENT ON COLUMN "VENDOR_MASTER"."CONTACT_EMAIL" IS '이메일';
COMMENT ON COLUMN "VENDOR_MASTER"."ADDRESS" IS '주소';
COMMENT ON COLUMN "VENDOR_MASTER"."SALES_REP_NAME" IS '영업대표 (1인)';
COMMENT ON COLUMN "VENDOR_MASTER"."SALES_REP_PHONE" IS '영업대표 전화번호';
COMMENT ON COLUMN "VENDOR_MASTER"."SALES_REP_EMAIL" IS '영업대표 이메일';
COMMENT ON COLUMN "VENDOR_MASTER"."SUPPORT_1_NAME" IS '담당자 1';
COMMENT ON COLUMN "VENDOR_MASTER"."SUPPORT_1_PHONE" IS '담당자 1 전화번호';
COMMENT ON COLUMN "VENDOR_MASTER"."SUPPORT_1_EMAIL" IS '담당자 1 이메일';
COMMENT ON COLUMN "VENDOR_MASTER"."SUPPORT_2_NAME" IS '담당자 2';
COMMENT ON COLUMN "VENDOR_MASTER"."SUPPORT_2_PHONE" IS '담당자 2 전화번호';
COMMENT ON COLUMN "VENDOR_MASTER"."SUPPORT_2_EMAIL" IS '담당자 2 이메일';


-- 서버 마스터 (Server)
CREATE TABLE "SERVER_MASTER" (
    "SERVER_ID"     NUMBER GENERATED BY DEFAULT AS IDENTITY PRIMARY KEY,  -- 서버 ID
    "SERVER_NAME"   VARCHAR2(200) NOT NULL,                 -- 서버명
    "HOSTNAME"      VARCHAR2(255),                         -- 호스트명
    "IP_ADDRESS"    VARCHAR2(45),                          -- IP 주소
    "OS_TYPE"       VARCHAR2(100),                         -- OS 유형
    "OS_VERSION"    VARCHAR2(100),                          -- OS 버전
    "CPU_CORES"     NUMBER(10),                             -- CPU 코어 수
    "CPU_MODEL"     VARCHAR2(200),                         -- CPU 모델명
    "MEMORY_GB"     NUMBER(10,2),                           -- 메모리(GB)
    "LOCATION"      VARCHAR2(200),                          -- 물리적 위치
    "DESCRIPTION"   CLOB,                                   -- 설명
    "STATUS"        VARCHAR2(20) DEFAULT 'ACTIVE',          -- 상태 (ACTIVE/INACTIVE 등)
    "STAFF_1_ID"    NUMBER(10) REFERENCES "STAFF_MASTER"("STAFF_ID"),   -- 담당 스태프 1
    "STAFF_2_ID"    NUMBER(10) REFERENCES "STAFF_MASTER"("STAFF_ID"),   -- 담당 스태프 2
    "STAFF_3_ID"    NUMBER(10) REFERENCES "STAFF_MASTER"("STAFF_ID"),   -- 담당 스태프 3
    "STAFF_4_ID"    NUMBER(10) REFERENCES "STAFF_MASTER"("STAFF_ID"),   -- 담당 스태프 4
    "VENDOR_1_ID"   NUMBER(10) REFERENCES "VENDOR_MASTER"("VENDOR_ID"), -- 벤더 1
    "VENDOR_2_ID"   NUMBER(10) REFERENCES "VENDOR_MASTER"("VENDOR_ID"), -- 벤더 2
    "VENDOR_3_ID"   NUMBER(10) REFERENCES "VENDOR_MASTER"("VENDOR_ID"), -- 벤더 3
    "VENDOR_4_ID"   NUMBER(10) REFERENCES "VENDOR_MASTER"("VENDOR_ID"), -- 벤더 4
    "CREATED_AT"    TIMESTAMP DEFAULT SYSTIMESTAMP,         -- 등록일시
    "UPDATED_AT"    TIMESTAMP DEFAULT SYSTIMESTAMP          -- 수정일시
);

COMMENT ON TABLE "SERVER_MASTER" IS '서버 마스터';
COMMENT ON COLUMN "SERVER_MASTER"."SERVER_ID" IS '서버 ID';
COMMENT ON COLUMN "SERVER_MASTER"."SERVER_NAME" IS '서버명';
COMMENT ON COLUMN "SERVER_MASTER"."HOSTNAME" IS '호스트명';
COMMENT ON COLUMN "SERVER_MASTER"."IP_ADDRESS" IS 'IP 주소';
COMMENT ON COLUMN "SERVER_MASTER"."OS_TYPE" IS 'OS 유형';
COMMENT ON COLUMN "SERVER_MASTER"."OS_VERSION" IS 'OS 버전';
COMMENT ON COLUMN "SERVER_MASTER"."CPU_CORES" IS 'CPU 코어 수';
COMMENT ON COLUMN "SERVER_MASTER"."CPU_MODEL" IS 'CPU 모델명';
COMMENT ON COLUMN "SERVER_MASTER"."MEMORY_GB" IS '메모리(GB)';
COMMENT ON COLUMN "SERVER_MASTER"."LOCATION" IS '물리적 위치';
COMMENT ON COLUMN "SERVER_MASTER"."DESCRIPTION" IS '설명';
COMMENT ON COLUMN "SERVER_MASTER"."STATUS" IS '상태 (ACTIVE/INACTIVE 등)';
COMMENT ON COLUMN "SERVER_MASTER"."STAFF_1_ID" IS '담당 스태프 1';
COMMENT ON COLUMN "SERVER_MASTER"."STAFF_2_ID" IS '담당 스태프 2';
COMMENT ON COLUMN "SERVER_MASTER"."STAFF_3_ID" IS '담당 스태프 3';
COMMENT ON COLUMN "SERVER_MASTER"."STAFF_4_ID" IS '담당 스태프 4';
COMMENT ON COLUMN "SERVER_MASTER"."VENDOR_1_ID" IS '벤더 1';
COMMENT ON COLUMN "SERVER_MASTER"."VENDOR_2_ID" IS '벤더 2';
COMMENT ON COLUMN "SERVER_MASTER"."VENDOR_3_ID" IS '벤더 3';
COMMENT ON COLUMN "SERVER_MASTER"."VENDOR_4_ID" IS '벤더 4';


-- 솔루션 마스터 (Solution)
CREATE TABLE "SOLUTION_MASTER" (
    "SOLUTION_ID"   NUMBER GENERATED BY DEFAULT AS IDENTITY PRIMARY KEY,  -- 솔루션 ID
    "SOLUTION_NAME" VARCHAR2(200) NOT NULL,                 -- 솔루션명
    "VERSION"       VARCHAR2(50),                          -- 버전
    "VENDOR_ID"     NUMBER(10) REFERENCES "VENDOR_MASTER"("VENDOR_ID"), -- 제조/공급 벤더
    "DESCRIPTION"   CLOB,                                   -- 설명
    "STATUS"        VARCHAR2(20) DEFAULT 'ACTIVE',         -- 상태 (ACTIVE/INACTIVE 등)
    "STAFF_1_ID"    NUMBER(10) REFERENCES "STAFF_MASTER"("STAFF_ID"),   -- 담당 스태프 1
    "STAFF_2_ID"    NUMBER(10) REFERENCES "STAFF_MASTER"("STAFF_ID"),   -- 담당 스태프 2
    "STAFF_3_ID"    NUMBER(10) REFERENCES "STAFF_MASTER"("STAFF_ID"),   -- 담당 스태프 3
    "STAFF_4_ID"    NUMBER(10) REFERENCES "STAFF_MASTER"("STAFF_ID"),   -- 담당 스태프 4
    "VENDOR_1_ID"   NUMBER(10) REFERENCES "VENDOR_MASTER"("VENDOR_ID"), -- 벤더 1
    "VENDOR_2_ID"   NUMBER(10) REFERENCES "VENDOR_MASTER"("VENDOR_ID"), -- 벤더 2
    "VENDOR_3_ID"   NUMBER(10) REFERENCES "VENDOR_MASTER"("VENDOR_ID"), -- 벤더 3
    "VENDOR_4_ID"   NUMBER(10) REFERENCES "VENDOR_MASTER"("VENDOR_ID"), -- 벤더 4
    "CREATED_AT"    TIMESTAMP DEFAULT SYSTIMESTAMP,         -- 등록일시
    "UPDATED_AT"    TIMESTAMP DEFAULT SYSTIMESTAMP         -- 수정일시
);

COMMENT ON TABLE "SOLUTION_MASTER" IS '솔루션 마스터';
COMMENT ON COLUMN "SOLUTION_MASTER"."SOLUTION_ID" IS '솔루션 ID';
COMMENT ON COLUMN "SOLUTION_MASTER"."SOLUTION_NAME" IS '솔루션명';
COMMENT ON COLUMN "SOLUTION_MASTER"."VERSION" IS '버전';
COMMENT ON COLUMN "SOLUTION_MASTER"."VENDOR_ID" IS '제조/공급 벤더 (참조)';
COMMENT ON COLUMN "SOLUTION_MASTER"."DESCRIPTION" IS '설명';
COMMENT ON COLUMN "SOLUTION_MASTER"."STATUS" IS '상태 (ACTIVE/INACTIVE 등)';
COMMENT ON COLUMN "SOLUTION_MASTER"."STAFF_1_ID" IS '담당 스태프 1';
COMMENT ON COLUMN "SOLUTION_MASTER"."STAFF_2_ID" IS '담당 스태프 2';
COMMENT ON COLUMN "SOLUTION_MASTER"."STAFF_3_ID" IS '담당 스태프 3';
COMMENT ON COLUMN "SOLUTION_MASTER"."STAFF_4_ID" IS '담당 스태프 4';
COMMENT ON COLUMN "SOLUTION_MASTER"."VENDOR_1_ID" IS '벤더 1';
COMMENT ON COLUMN "SOLUTION_MASTER"."VENDOR_2_ID" IS '벤더 2';
COMMENT ON COLUMN "SOLUTION_MASTER"."VENDOR_3_ID" IS '벤더 3';
COMMENT ON COLUMN "SOLUTION_MASTER"."VENDOR_4_ID" IS '벤더 4';
